mapper.delete:
UPDATE LJP_QNABOARD SET QNA_ISDEL = 1 WHERE QNA_SEQ IN
 (SELECT QNA_SEQ FROM LJP_QNABOARD START WITH QNA_SEQ = #{qna_seq}
 CONNECT BY PRIOR QNA_SEQ = QNA_REFER)

mapper.select:
SELECT QNA_SEQ, QNA_TITLE, QNA_CONTENT, QNA_FILE_NAME, QNA_READCOUNT, QNA_REGDATE, MEM_ID
FROM LJP_QNABOARD
WHERE QNA_SEQ = #{qna_seq}
