mapper.update:
UPDATE LJP_MEMBER
SET (MEM_PW, MEM_IMAGE, MEM_NAME, MEM_ADDRESS, MEM_PHONE, MEM_B_NAME, MEM_B_STATUS) = (#{mem_pw}, #{mem_image}, #{mem_name}, #{mem_address}, #{mem_phone}, #{mem_b_name}, #{mem_b_status})
WHERE MEM_ID = #{mem_id}

mapper.select:
SELECT MEM_ID,MEM_PW
from LJP_MEMBER
where MEM_ID=#{mem_id} and MEM_PW=#{mem_pw}

SELECT MEM_ID
FROM LJP_MEMBER
WHERE MEM_ID = #{mem_id}

SELECT MEM_EMAIL
FROM LJP_MEMBER
WHERE MEM_EMAIL = #{mem_email}

SELECT MEM_ID
FROM LJP_MEMBER
WHERE MEM_EMAIL = #{mem_email} AND MEM_NAME = #{mem_name}

SELECT MEM_PW
FROM LJP_MEMBER
WHERE MEM_ID = #{mem_id} AND MEM_NAME = #{mem_name} AND MEM_EMAIL = #{mem_email}
